import UIKit

enum Login {
    enum User {
        struct Request {
            let user: String
            let password: String
        }
        
        struct Response: Codable {
            let userId: Int
            let name: String
            let bankAccount: String
            let agency: String
            let balance: Double
            
            enum CodingKeys: String, CodingKey {
                case userId = "userId"
                case name = "name"
                case bankAccount = "bankAccount"
                case agency = "agency"
                case balance = "balance"
            }
        }
        
        struct ViewModel { }
    }
}
