import UIKit

enum Statement
{
    enum User {
        struct Request { }
        struct Response { }
        
        struct ViewModel {
            let name: String
            let bankAccount: String
            let agency: String
            
            init(from model: Login.User.Response) {
                name = model.name
                bankAccount = model.bankAccount
                agency = model.agency
            }
        }
    }
    
    enum Something {
        struct Request { }
        
        struct Response: Codable {
            let statementList: [Transaction]
        }
        
        struct Transaction: Codable {
            let title: String
            let description: String
            let date: String
            let value: Int
            
            enum CodingKeys: String, CodingKey {
                case title = "title"
                case description = "desc"
                case date = "date"
                case value = "value"
            }
        }
        
        struct ViewModel {
            let title: String
            let description: String
            let date: Date
            let value: Int
        }
    }
}
